var http = require("http"),
    url = require("url"),
    router = require('router'),
    port = process.env.PORT || 1313;

    //session = require('session');
    //path = require("path"),
    //fs = require("fs"),
    //qs = require("querystring"),
    //odata = require("osql"),

function start(route, handle) {
  var wp = '';
  var pathname = '';
  http.createServer(function(request, response) {
	 
    wp = url.parse(request.url).pathname.split('/')[1];
    pathname = (wp === '') ? '/c4h' : url.parse(request.url).pathname;
	//var pathname = (wp === 'orthocoder10D') ? '/' : url.parse(request.url).pathname;
    console.log("wp = " + wp + " : pathname = " + pathname);
    route(handle, pathname, request, response);
    
  }).listen(port);
  console.log("wp = " + wp + " : pathname = " + pathname);
  console.log("Static file server running at:\n  => http://localhost:" + port + "/\nCTRL + C to shutdown");
}

exports.start = start;
